-- ----------------------------
-- Table structure for songlist
-- ----------------------------
DROP TABLE IF EXISTS `songlist`;
CREATE TABLE `songlist`  (
  `songListId` int(11) NOT NULL AUTO_INCREMENT COMMENT '歌单ID',
  `name` varchar(50) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL COMMENT '歌单名称',
  `userId` int(11) NOT NULL COMMENT '用户ID',
  `accessCount` int(11) NOT NULL DEFAULT 0 COMMENT '热度，访问次数，默认为0',
  `introduction` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL COMMENT '歌单简介',
  `collectionCount` int(11) NOT NULL COMMENT '收藏次数',
  `tags` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL COMMENT '歌单标签',
  `photoUrl` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL COMMENT '歌单图标',
  `createDate` datetime(0) NOT NULL COMMENT '歌单创建日期',
  `updateDate` datetime(0) NOT NULL COMMENT '歌单更新日期',
  PRIMARY KEY (`songListId`) USING BTREE,
  INDEX `FK_Reference_5`(`userId`) USING BTREE,
  CONSTRAINT `FK_Reference_5` FOREIGN KEY (`userId`) REFERENCES `user` (`userId`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8 COLLATE = utf8_unicode_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for songlistwithsong
-- ----------------------------
DROP TABLE IF EXISTS `songlistwithsong`;
CREATE TABLE `songlistwithsong`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '歌单歌曲ID',
  `songListId` int(11) NOT NULL COMMENT '歌单编号，外键',
  `songId` int(11) NOT NULL COMMENT '歌曲编号，外键',
  `createTime` datetime(0) NOT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `FK_Reference_6`(`songListId`) USING BTREE,
  INDEX `FK_Reference_7`(`songId`) USING BTREE,
  CONSTRAINT `FK_Reference_6` FOREIGN KEY (`songListId`) REFERENCES `songlist` (`songListId`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `FK_Reference_7` FOREIGN KEY (`songId`) REFERENCES `song` (`songId`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8 COLLATE = utf8_unicode_ci ROW_FORMAT = Dynamic;

-- ----------------------------
